import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return (
    <>
    <h1>
      <p>{props.text}</p>
    </h1>
    </>

  )
}

const Content = (props) => {
  return (
    <>
      <p>{props.text} {props.amount}</p>
    </>
  )
}

const Total = (props) => {
  return (
    <>
      {props.amount}
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header text={course} />

        <Content text={part1} amount={exercises1} />


        <Content text={part2} amount={exercises2} />


        <Content text={part3} amount={exercises3} />

      <p>Number of exercises <Total amount={exercises1 + exercises2 + exercises3} /> </p>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
