import React, { useState } from 'react'
import Person from './components/Person'

const App = () => {
  const [ persons, setPersons] = useState([
    { name: 'Arto Hellas', number: 911 }
  ]) 
  const [ newName, setNewName ] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')   



const addName = (event) => {

  var nameE = false

  event.preventDefault()
  const nameObject = {
    name: newName,
    number: newNumber
  }

  const sameName = persons.map((person) => {
  	if (person.name === newName){
  		//console.log("ALERT: Name already exists")
  		nameE = true
  	}
  })
  //console.log(nameE)
  if (!nameE){
  	setPersons(persons.concat(nameObject))
  	setNewName('')
  	setNewNumber('')
  	//console.log("Concatenaded")

  } 

}

const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

const handleFilter = (event) =>{
	setNewFilter(event.target.value)
	console.log(event.target.value)
}
  return (
    <div>
      <h2>Phonebook</h2>
      <input value={newFilter} onChange={handleFilter} />
      <p></p>
      <h3> Add New </h3> <p></p>
      <form onSubmit={addName}>
        <div>
          name: <input value={newName} onChange={handleNameChange}/>
        </div>
          <div>
          phone number: <input value={newNumber} onChange={handleNumberChange}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {persons.filter(x => x.name.includes(newFilter)).map((person, i) => 
          <Person key={i} person={person} />
        )}
      </ul>
    </div>
  )

}

export default App
