import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return (
    <>
    <h1>
      <p>{props.text}</p>
    </h1>
    </>

  )
}

const Part = (props) => {
  return (
    <>
      <p>{props.text} {props.amount}</p>
    </>
  )
}

const Content = (props) =>{
	return(
	<>
	  <Part text={props.text1} amount={props.amount1} />   
      <Part text={props.text2} amount={props.amount2} />       
      <Part text={props.text3} amount={props.amount3} /> 
    </>
	)
}

const Total = (props) => {
  return (
    <>
      Number of exercises {props.amount}
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }


  return (
    <div>
      <Header text={course} />   
      <Content text1={part1.name} amount1={part1.exercises} text2={part2.name} amount2={part2.exercises} text3={part3.name} amount3={part3.exercises}/>   
      <Total amount={part1.exercises + part2.exercises + part3.exercises} />
    </div>
  )
}


ReactDOM.render(<App />, document.getElementById('root'))
