import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistics = ({good, bad, neutral, total, average, positive}) => {


	  return(

  	  <>
	  <h2> Statistics </h2>

      Good {good} <p></p>
      Neutral {neutral} <p></p>
      Bad {bad} <p></p>
      All {total} <p></p>
      Average {average} <p></p>
      Positive {positive} <p></p>

      </>
    )
}

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [total, setTotal] = useState(0)

  const updateGood = () => {
		setGood(good + 1);
		setTotal(total + 1);
	}

  const updateNeutral = () => {
		setNeutral(neutral + 1);
		setTotal(total + 1);
	}	

  const updateBad = () => {
		setBad(bad + 1);
		setTotal(total + 1);
	}
  const average = (good - bad) / total;
  const positive = good / total;
  console.log(average)
  return (
    <div>
      <h1> Give Feedback </h1>
      <p></p>
      <button onClick={() => updateGood()}>
      Good
      </button>
      <button onClick={() => updateNeutral()}>
      Neutral
      </button> 
      <button onClick={() => updateBad()}>
      Bad
      </button>
      <p></p>
      <Statistics good={good} bad={bad} neutral={neutral} total={total} average={average} positive={positive} />
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
