import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return (
    <>
    <h1>
      <p>{props.text}</p>
    </h1>
    </>

  )
}

const Part = (props) => {
  return (
    <>
      <p>{props.text} {props.amount}</p>
    </>
  )
}

const Content = (props) =>{
	return(
	<>
	  <Part text={props.text1} amount={props.amount1} />   
      <Part text={props.text2} amount={props.amount2} />       
      <Part text={props.text3} amount={props.amount3} /> 
    </>
	)
}

const Total = (props) => {
  return (
    <>
      Number of exercises {props.amount}
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header text={course} />   
      <Content text1={part1} amount1={exercises1} text2={part2} amount2={exercises2} text3={part3} amount3={exercises3}/>   

      <Total amount={exercises1 + exercises2 + exercises3} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
